theme_opts.create_style:
pre, pre * {font-family: monospace !important;} .tooltip * {color: black !important}

nav {
  background: linear-gradient(to right, #088, #0ca);
  padding: 10px;
}
nav, nav * {
  box-sizing: border-box;
}
nav * {
  color: white;
}
div.search-box-input-container {
  background: #fff4;
  border-radius: 5px;
}
nav ::placeholder {
  color: #ddd;
}
nav svg {
  fill: white;
}
.methods {
  background: #0001;
}
.accordion-content {
  background: #fff1;
}
div.accordion-heading {
  background: #fff3;
}

::-webkit-scrollbar {
  width: 3px;
}
::-webkit-scrollbar-track{
  background: #fff1;
}

nav ::-webkit-scrollbar-thumb{
  border-radius: 1000px;
  background: #fff3;
}

.page-title, header * {
  border-color: #0ca5;
}
article pre div, article pre div * {
  background: white;
  color: #333;
}
li.p-h-n {
  color: #333 !important;
}
#search-item-ul * {
  color: #333 !important;
}
#search-item-ul {
  background: #fff9;
  backdrop-filter: blur(10px);
  border-radius: 3px;
}

theme_opts.add_scripts:
setInterval(() => {
  try {
    document.querySelector('.os-content-arrange').remove();
  } catch (e) {}

  var els = Array.from(document.querySelectorAll('ul.accordion-content a')).filter(el => el.innerText.startsWith("exports."));
  for (var i = 0; i < els.length; i++) {
    els[i].innerText = els[i].innerText.replace(/^exports\./, '');
  }
}, 100);
